import * as types from "./itemTypes";
import * as selectors from "./itemSelectors";
import fetchAPI from "../fetchAPI";
import axios from "axios";

export const getItems = () => async (dispatch) => {
  dispatch({ type: types.FETCH_ITEMS_REQUEST, payload: false });
  const response = fetchAPI.fetchItems;
  const payload = response.json();
  if (response.status === 200) {
    dispatch({ type: types.FETCH_ITEMS_SUCCESS, payload: payload });
    console.log(payload);
  }
};
export const fetchItemsRequest = () => {
  return {
    type: types.FETCH_ITEMS_REQUEST,
  };
};
export const fetchItemsSuccess = (items) => {
  return {
    type: types.FETCH_ITEMS_SUCCESS,
    payload: items,
  };
};
export const fetchItemsFailure = (error) => {
  return {
    type: types.FETCH_ITEMS_FAILURE,
    payload: error,
  };
};

export const fetchItems = () => {
  return (dispatch) => {
    dispatch(fetchItemsRequest);
    axios
      .get("http://127.0.0.1:4000/items")
      .then((response) => {
        const items = response.data;
        dispatch(fetchItemsSuccess(items));
      })
      .catch((error) => {
        const errorMsg = error.message;
        dispatch(fetchItemsFailure(errorMsg));
      });
  };
};
