import React, { useEffect } from "react";
import { connect, useSelector, useDispatch } from "react-redux";
import { fetchItems } from "../../content/items/itemActions";
import ItemCard from "../../components/itemCard/index.tsx";
import content from "../../content/items";
function Home({ fetchItems, itemsData }) {
  const dispatch = useDispatch();
  const items = useSelector((state) =>
    content.items.itemSelectors.getItems(state)
  );
  useEffect(async () => {
    dispatch(items);
    fetchItems();
  }, []);
  console.log(itemsData, itemsData.items);
  return itemsData.loading ? (
    <h2>loading</h2>
  ) : itemsData.error ? (
    <h2>error</h2>
  ) : (
    <div>
      {itemsData?.items &&
        itemsData?.items?.map((item) => {
          return <ItemCard title={item.name} price={item.price}></ItemCard>;
        })}
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    itemsData: state.items,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    fetchItems: () => dispatch(fetchItems()),
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(Home);
